import React, { useState } from "react";

function BotCollection({ bolt }) {
  const [bot, setBots] = useState(bolt);

  async function addBot(index) {
    setBots(bolt.index);
    console.log(bot);
  }

  return (
    <div className="ui four column grid">
      {bolt.map((bolts) => {
        return (
          <div className="row">
            <div onClick={() => addBot(bolt.index)}>
              <img className="images" src={bolts.avatar_url} alt={bolts.name} />
              <p> {bolts.name}</p>
              <span>{bolts.health}</span>
              <span>{bolts.damage}</span>
              <span>{bolts.armor}</span>
            </div>
          </div>
        );
      })}
    </div>
  );
}
export default BotCollection;
